mapper.sql:
FROM MCNEWGOODREQUEST GOODREQ WITH(NOLOCK) LEFT OUTER JOIN ATTACHINFO ATT1 WITH(NOLOCK) ON GOODREQ.FIRSTATTACHSEQ = ATT1.attach_seq LEFT OUTER JOIN ATTACHINFO ATT2 WITH(NOLOCK) ON GOODREQ.SECONDATTACHSEQ = ATT2.attach_seq LEFT OUTER JOIN ATTACHINFO ATT3 WITH(NOLOCK) ON GOODREQ.THIRDATTACHSEQ = ATT3.attach_seq LEFT OUTER JOIN MCBID BID WITH(NOLOCK) ON BID.NEWGOODID = GOODREQ.NEWGOODID , SMPUSERS WITH(NOLOCK) WHERE GOODREQ.insert_user_id = SMPUSERS.USERID AND GOODREQ.delete_state = '0'
AND GOODREQ.insert_borgid IN (SELECT BORGID FROM SMPBORGS WITH(NOLOCK) WHERE GROUPID = #{srcGroupId} AND BORGTYPECD = 'BCH')
AND GOODREQ.insert_borgid IN (SELECT BORGID FROM SMPBORGS WITH(NOLOCK) WHERE CLIENTID = #{srcClientId} AND BORGTYPECD = 'BCH')
AND GOODREQ.insert_borgid = #{srcBranchId}
AND GOODREQ.insert_date >= CONVERT(DATETIME, #{srcInsert_FromDt})
AND GOODREQ.insert_date <= CONVERT(DATETIME, #{srcInsert_EndDt})+1
AND GOODREQ.stand_good_name LIKE '%' + #{srcStand_good_name} + '%'
AND GOODREQ.state = #{srcState}
AND GOODREQ.stand_good_spec_desc LIKE '%' + #{srcStand_good_spec_desc} + '%'
AND GOODREQ.insert_user_id = #{srcInsertUserId}

mapper.select:
/* product.newProductBid.selectNewProductRequestListCnt */ SELECT COUNT(*) from ( select 1 as cnt
group by GOODREQ.stand_good_name , GOODREQ.stand_good_spec_desc , CONVERT(VARCHAR(10), GOODREQ.insert_date, 121) , GOODREQ.state , GOODREQ.note , GOODREQ.insert_borgid , GOODREQ.good_iden_numb , dbo.FNS_CODENM1BYCODEVAL1('NEW_PROD_STATE',GOODREQ.state) , dbo.FNS_BORG_STRING(GOODREQ.insert_borgid) , SMPUSERS.USERNM , GOODREQ.newgoodid , GOODREQ.request_type , ATT1.attach_seq , ATT2.attach_seq , ATT3.attach_seq , ATT1.attach_file_name , ATT2.attach_file_name , ATT3.attach_file_name , ATT1.attach_file_path , ATT2.attach_file_path , ATT3.attach_file_path
) a

/* product.newProductBid.selectNewProductRequestList */ SELECT A.* FROM ( select GOODREQ.stand_good_name , GOODREQ.stand_good_spec_desc , CONVERT(VARCHAR(10), GOODREQ.insert_date, 121) AS INSERT_DATE , GOODREQ.state , GOODREQ.note , GOODREQ.good_iden_numb , GOODREQ.insert_user_id , dbo.FNS_BorgNameByBorgID( GOODREQ.insert_borgid ) AS userBorgNm , GOODREQ.insert_borgid , ATT1.attach_seq AS firstattachseq , ATT2.attach_seq AS secondattachseq , ATT3.attach_seq AS thirdattachseq , ATT1.attach_file_name AS firstAttachName , ATT2.attach_file_name AS secondAttachName , ATT3.attach_file_name AS thirdAttachName , ATT1.attach_file_path AS attach_file_path1 , ATT2.attach_file_path AS attach_file_path2 , ATT3.attach_file_path AS attach_file_path3 , dbo.FNS_CODENM1BYCODEVAL1('NEW_PROD_STATE',GOODREQ.state) AS stateNm
, ISNULL(BID.bidid , '' ) AS BIDID
, ISNULL(max(BID.bidid) , '' ) AS BIDID
, dbo.FNS_BORG_STRING(GOODREQ.insert_borgid) AS borgNms , SMPUSERS.USERNM AS userId , GOODREQ.newgoodid , GOODREQ.request_type
group by GOODREQ.stand_good_name , GOODREQ.stand_good_spec_desc , CONVERT(VARCHAR(10), GOODREQ.insert_date, 121) , GOODREQ.state , GOODREQ.note , GOODREQ.insert_user_id , GOODREQ.insert_borgid , GOODREQ.good_iden_numb , dbo.FNS_CODENM1BYCODEVAL1('NEW_PROD_STATE',GOODREQ.state) , dbo.FNS_BORG_STRING(GOODREQ.insert_borgid) , SMPUSERS.USERNM , GOODREQ.newgoodid , GOODREQ.request_type , ATT1.attach_seq , ATT2.attach_seq , ATT3.attach_seq , ATT1.attach_file_name , ATT2.attach_file_name , ATT3.attach_file_name , ATT1.attach_file_path , ATT2.attach_file_path , ATT3.attach_file_path
) A
ORDER BY ${orderString}

/* product.newProductBid.selectNewProductBidListCntVen */ SELECT COUNT(1) FROM MCBID WITH(NOLOCK) , SMPUSERS WITH(NOLOCK) , MCBIDAUCTION WITH(NOLOCK) WHERE MCBID.insert_user_id = SMPUSERS.USERID AND MCBID.BIDID = MCBIDAUCTION.BIDID AND MCBIDAUCTION.VENDORID = #{srcVendorid}
AND MCBID.BIDID LIKE '%' + #{srcBidid} + '%'
AND MCBID.BIDNAME LIKE '%' + #{srcBidname} + '%'
AND MCBID.BIDSTATE = #{srcBidState}
AND MCBID.STAND_GOOD_NAME LIKE '%' + #{srcStand_good_name} + '%'
AND MCBID.INSERT_DATE >= CONVERT(DATETIME, #{srcInsert_FromDt})
AND MCBID.INSERT_DATE <= CONVERT(DATETIME, #{srcInsert_EndDt})+1
AND MCBID.STAND_GOOD_SPEC_DESC LIKE '%' + #{srcStand_good_spec_desc} + '%'
AND MCBIDAUCTION.VENDORID = #{srcVendorid}
AND MCBID.BID_CLASSIFY = #{bidClassify}

/* product.newProductBid.selectNewProductBidList */ SELECT A.BIDID , A.BIDNAME , A.STAND_GOOD_NAME , A.STAND_GOOD_SPEC_DESC , A.BIDSTATE , A.BIDSTATENM , A.BIDENDDATE , A.INSERT_USER_ID , A.INSERT_DATE , A.IS_USE_CERTIFICATE
, A.VENDORID
, A.VENDORBIDSTATE , CASE WHEN (B.CODENM1 IS NULL OR B.CODENM1 = '') THEN '°ø¿ë' ELSE B.CODENM1 END BID_CLASSIFY FROM( SELECT MCBID.BIDID , MCBID.BIDNAME , MCBID.STAND_GOOD_NAME , MCBID.STAND_GOOD_SPEC_DESC , MCBID.BIDSTATE , dbo.FNS_CODENM1BYCODEVAL1('BIDSTATE',MCBID.BIDSTATE) AS BIDSTATENM , CONVERT(VARCHAR(16), MCBID.BIDENDDATE, 120) AS BIDENDDATE , SMPUSERS.USERNM AS INSERT_USER_ID , CONVERT(VARCHAR(16), MCBID.INSERT_DATE, 121) AS INSERT_DATE , MCBID.IS_USE_CERTIFICATE
, MCBIDAUCTION.VENDORID
, MAX(MCBIDAUCTION.VENDORBIDSTATE) VENDORBIDSTATE , MCBID.BID_CLASSIFY
)A LEFT OUTER JOIN SMPCODES B ON A.BID_CLASSIFY = B.CODEVAL1 AND CODETYPECD = 'BID_CLASSIFY'
ORDER BY ${orderString}

mapper.insert:
/*	product.newProductBid.insertNewProductRequest*/
INSERT INTO mcnewgoodrequest
   (
       newgoodid
     , stand_good_name
     , request_type
     , stand_good_spec_desc
     , note
     , state
     , insert_user_id
     , insert_borgid
     , insert_date
     , firstattachseq
     , secondattachseq
     , thirdattachseq
     , delete_state
   )
   VALUES
  (  #{newgoodid}
   , #{stand_good_name}
   , #{request_type}
   , #{stand_good_spec_desc}
   , #{note}
   , #{state}
   , #{insert_user_id}
   , #{insert_borgid}
   , getdate()
   , #{firstattachseq}
   , #{secondattachseq}
   , #{thirdattachseq}
   , #{delete_state}
)

mapper.update:
/*	product.newProductBid.updateNewProductRequest */
UPDATE	MCNEWGOODREQUEST
SET	STAND_GOOD_NAME = #{stand_good_name}
,		REQUEST_TYPE = #{request_type}
,		STAND_GOOD_SPEC_DESC = #{stand_good_spec_desc}
,		NOTE = #{note}
,		STATE = #{state}
,		FIRSTATTACHSEQ = #{firstattachseq}
,		SECONDATTACHSEQ = #{secondattachseq}
,		THIRDATTACHSEQ = #{thirdattachseq}
,		INSERT_USER_ID = #{insert_user_id}
,		INSERT_BORGID = #{insert_borgid}
,		INSERT_DATE = GETDATE()
,		DELETE_STATE = #{delete_state}
WHERE	NEWGOODID = #{newgoodid}

/*	product.newProductBid.updateExistsProductProcess*/
UPDATE	mcnewgoodrequest
SET	state = #{state}
,		set_user_id = #{insert_user_id}
,		set_date = GETDATE()
,		good_iden_numb = #{good_iden_numb}
 WHERE  newgoodid     = #{newgoodid}

/*	product.newProductBid.updateNewProductRequestState */
UPDATE	MCNEWGOODREQUEST
SET	STATE = #{state}
,		SET_USER_ID = #{insert_user_id}
,		SET_DATE = GETDATE()
WHERE	NEWGOODID = #{newgoodid}

mapper.insert:
/*	product.newProductBid.insertBid */
INSERT INTO MCBID (
		BIDID
,		BIDNAME
,		BIDSTATE
,		STAND_GOOD_NAME
,		STAND_GOOD_SPEC_DESC
,		IS_USE_CERTIFICATE
,		HOPE_SALE_PRICE
,		BIDNOTE
,		FIRSTATTACHSEQ
,		SECONDATTACHSEQ
,		THIRDATTACHSEQ
,		BIDENDDATE
,		NEWGOODID
,		INSERT_USER_ID
,		INSERT_DATE
,		BID_CLASSIFY
) VALUES (
		#{bidid}
,		#{bidname}
,		#{bidstate}
,		#{stand_good_name}
,		#{stand_good_spec_desc}
,		#{is_use_certificate}
,		#{hope_sale_price}
,		#{bidnote}
,		#{firstattachseq}
,		#{secondattachseq}
,		#{thirdattachseq}
,		#{bidenddate}
,		#{newgoodid}
,		#{insert_user_id}
,		#{bidStartdate}
,		#{bidClassify}
)

/*	product.newProductBid.insertBidAuction */
INSERT INTO MCBIDAUCTION (
		BIDID
,		VENDORID
,		VENDORBIDSTATE
,		IS_REG_GODD
,		CONFIRM_DATE
,		CONFIRM_USER_ID
) VALUES (
		#{bidid}
,		#{vendorid}
,		#{vendorbidstate}
,		#{IS_REG_GODD}
,		GETDATE()
,		#{insert_user_id}
)

mapper.select:
/* product.newProductBid.selectNewProductBidAuctionListCnt */
SELECT COUNT(1)
FROM	MCBIDAUCTION BIDAUC WITH(NOLOCK) 
,		SMPVENDORS VEN WITH(NOLOCK) 
WHERE	BIDAUC.VENDORID = VEN.VENDORID
AND	BIDAUC.BIDID = #{bidid}

mapper.update:
/*	product.newProductBid.updateBidAuction */
UPDATE	MCBIDAUCTION
SET	VENDORBIDSTATE = #{vendorbidstate}
,		GOOD_NAME = #{good_name}
,		GOOD_SPEC_DESC = #{good_spec_desc}
,		ORDE_CLAS_CODE = #{orde_clas_code}
,		SALE_UNIT_PRICE = #{sale_unit_price}
,		DELI_MINI_DAY = #{deli_mini_day}
,		DELI_MINI_QUAN = #{deli_mini_quan}
,		MAKE_COMP_NAME = #{make_comp_name}
,		ORIGINAL_IMG_PATH = #{original_img_path}
,		LARGE_IMG_PATH = #{large_img_path}
,		MIDDLE_IMG_PATH = #{middle_img_path}
,		SMALL_IMG_PATH = #{small_img_path}
,		GOOD_DESC = #{good_desc}
,		GOOD_SAME_WORD = #{good_same_word}
,		FIRSTATTACHSEQ = #{FIRSTATTACHSEQ}
,		SECONDATTACHSEQ = #{SECONDATTACHSEQ}
,		THIRDATTACHSEQ = #{THIRDATTACHSEQ}
,		BIDDING_DATE = GETDATE()
,		BIDDING_USER_ID = #{bidding_user_id}
WHERE	BIDID = #{bidid}
AND	VENDORID = #{vendorid}

/*	product.newProductBid.updateBidAuctionState */
UPDATE	MCBIDAUCTION
SET	VENDORBIDSTATE = #{vendorbidstate}
,		CONFIRM_DATE = GETDATE()
,		CONFIRM_USER_ID = #{insert_user_id}
WHERE	BIDID = #{bidid}
AND	VENDORID = #{vendorid}

/*	product.newProductBid.updateBidState */
UPDATE	MCBID
SET	BIDSTATE = #{bidstate}
,		INSERT_USER_ID = #{insert_user_id}
WHERE	BIDID = #{bidid}

/*	product.newProductBid.updateRegistratGoodIdenNumb */
UPDATE	mcbidauction
   SET	IS_REG_GODD = '1'
 WHERE	BIDID 		= #{bidid}
   AND  vendorid 	= #{bid_vendor}

/*	product.newProductBid.updateBidGoodIdenNumb */
UPDATE mcbid 
   SET good_iden_numb = #{good_iden_numb}
 WHERE bidid          = #{bidid}

/*	product.newProductBid.updateNewProdGoodIdenNumb */
UPDATE mcnewgoodrequest 
   SET state          = #{mcnewgoodState}
     , good_iden_numb = #{good_iden_numb}
 WHERE newgoodid      = #{newgoodid}

/* product.newProductBid.newProductCancel */
UPDATE MCNEWGOODREQUEST SET
	SET_USER_ID = #{userId}
,	STATE = #{state}
,	SET_DATE = GETDATE()
WHERE NEWGOODID = #{newGoodId}
